const ribon={init(){this.cacheDom(),this.calcOffset(),this.bindEvents(),this.render()},cacheDom(){this.outer=document.querySelector(".info"),this.inner=this.outer.querySelector(".info__inner")},calcOffset(){this.offsetDif=this.inner.offsetWidth-this.outer.offsetWidth},setMouseCords(a){this.percentLeft=a.clientX/window.innerWidth,this.offset=this.offsetDif*this.percentLeft,this.render()},bindEvents(){document.addEventListener("mousemove",a=>this.setMouseCords(a)),document.addEventListener("resize",a=>this.setMouseCords(a))},render(){this.inner.style.transform=`translatex(-${this.offset}px)`}};ribon.init();
